mapper.select:
select * from TASK_RAW_PULSE
and TASK_ID = #{taskId}
order by CSV_ID, SERIAL_NO, ID

select *
from TASK_RAW_PULSE


        and TASK_ID = #{taskId}


        and INTERCEPT_TIME = between #{conditionParam.startTime} and #{conditionParam.endTime}


        and POSITION = between #{conditionParam.startPos} and #{conditionParam.endPos}


        and FREQUENCY = between #{conditionParam.startFrequency} and #{conditionParam.endFrequency}


        and PULSE_WIDTH = between #{conditionParam.startPulseWidth} and #{conditionParam.endPulseWidth}


        and MODULATION_TYPE = between #{conditionParam.startAmplitude} and #{conditionParam.endAmplitude}


order by CSV_ID, SERIAL_NO, ID
